<?php $fields = array( 'first_name', 'last_name', 'address_1', 'address_2', 'city', 'country', 'state', 'zip', 'shipping', 'nonce' ); ?>
<?php foreach( it_exchange_get_template_part_elements( 'super_widget_billing_address', 'fields', $fields ) as $field ) : ?>
